// ProtectedRoute.tsx
import React, { useContext } from 'react';
import { Route, Navigate, RouteProps } from 'react-router-dom';
import { AuthContext } from './AuthContext'; // import your AuthContext

interface ProtectedRouteProps extends RouteProps {
    authenticationPath: string;
}

export const ProtectedRoute: React.FC<ProtectedRouteProps> = ({ authenticationPath }) => {
    const { isLoggedIn } = useContext(AuthContext); // use the useContext hook to get the isLoggedIn value

    let redirectPath = '';
    if (!isLoggedIn) {
        redirectPath = props.authenticationPath;
    }

    if (redirectPath) {
        const renderComponent = () => <Navigate to={{ pathname: redirectPath }} />;
        return <Route {...props} component={renderComponent} render={undefined} />;
    } else {
        return <Route {...props} />;
    }
};
