export default class ExpressAPI {
  createUser = async (data: Object): Promise<Response> => {
    const response = await fetch('http://localhost:3001/api/auth/register', {
      method: 'post',
      body: JSON.stringify(data),
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
    })
    return this.checkStatus(response)
  }

  logUserIn = async (data: object): Promise<Response> => {
    const response = await fetch('http://localhost:3001/api/auth/login', {
      method: 'post',
      body: JSON.stringify(data),
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      }
    })
    return this.checkStatus(response)
  }

  fieldExistsInDB = async (fieldName: string, value: any): Promise<Response> => {
    const response = await fetch(`http://localhost:3001/api/user/${fieldName}/${value}`, {
      method: 'get',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      }
    })
    return this.checkStatus(response)
  }

  private checkStatus(response: Response): Response {
    if (response.status >= 200 && response.status < 300) {
        return response;
    } else {
        const message = response.json()
        errorMessage
        const error = new MyError(`HTTP Error ${response.json()}`);
        error.status = response.statusText;
        error.response = response;
        console.log(error);
        throw error;
    }
  }
}

class MyError extends Error {
  status?: string;
  response?: Response;
}
