import MainLayout from "./MainLayout"

export const ActiveGame: React.FC<{}> = () => {
  return (<MainLayout><></></MainLayout>)
}

const grid: string[][] = [
    ['A7', 'A6', 'A5', 'A4', 'A3', 'A2', 'A1', 'A0'],
    ['B7', 'B6', 'B5', 'B4', 'B3', 'B2', 'B1', 'B0'],
    ['C7', 'C6', 'C5', 'C4', 'C3', 'C2', 'C1', 'C0'],
    ['D7', 'D6', 'D5', 'D4', 'D3', 'D2', 'D1', 'D0'],
    ['E7', 'E6', 'E5', 'E4', 'E3', 'E2', 'E1', 'E0'],
    ['F7', 'F6', 'F5', 'F4', 'F3', 'F2', 'F1', 'F0'],
    ['G7', 'G6', 'G5', 'G4', 'G3', 'G2', 'G1', 'G0'],
    ['H7', 'H6', 'H5', 'H4', 'H3', 'H2', 'H1', 'H0']
];

interface GameState {
    [key: string]: [Piece | null, number];
}

export class Chess {
    private player1: Player;
    private p2: Player;
    private gameState: GameState;

    constructor(p1: Player, p2: Player) {
        this.p1 = p1;
        this.p2 = p2;
        this.gameState = {};
    }

    public initializeGame(): void {
        this.getPlayerNames();
        this.produceEmptyBoard();
        this.randomlyAssignWhite();
        this.setPiecesOnBoard();
    }

    public processMove(start: string, end: string): boolean {
        const copyState = { ...this.gameState };
        const adjStart = start[0] + ((parseInt(start[1]) - 1).toString());
        const adjEnd = end[0] + ((parseInt(end[1]) - 1).toString());
        const startCol = this.gameState[adjStart][1];
        const startRow = 7 - parseInt(adjStart[1]);

        const piece = copyState[adjStart][0];
        let allMoves = [];

        if (piece instanceof Pawn) {
            allMoves = this.p1.findPawnMoves(grid, this.gameState, startCol, startRow);
        } else if (piece instanceof Knight) {
            allMoves = piece.validKnightMoves(grid, this.gameState, startCol, startRow);
        } else if (piece instanceof Rook) {
            allMoves = piece.getAllStraight(grid, this.gameState, startCol, startRow);
        } else if (piece instanceof Bishop) {
            allMoves = piece.getAllDiagonal(grid, this.gameState, startCol, startRow);
        } else if (piece instanceof Queen) {
            allMoves = piece.getAllStraight(grid, this.gameState, startCol, startRow).concat(
                piece.getAllDiagonal(grid, this.gameState, startCol, startRow)
            );
        } else if (piece instanceof King) {
            allMoves = piece.validKingMoves(grid, this.gameState, startCol, startRow);
        }

        // The rest of this method would be as in the Python code...

        return false;
    }

    public printBoard(): void {
        // Similar to the Python code...
    }

    private getPlayerNames(): void {
        // Depends on your environment...
    }

    private setPiecesOnBoard(): void {
        const allPieces = this.p1.alive.concat(this.p2.alive);
        const allPositions = allPieces.map(p => p.position);
        for (const spot in this.gameState) {
            if (allPositions.includes(spot)) {
                for (const p of allPieces) {
                    if (p.position === spot) {
                        this.gameState[spot][0] = p;
                    }
                }
            }
        }
    }

    private randomlyAssignWhite(): void {
        const r = Math.floor(Math.random() * 2);
        if (r === 0) {
            this.p1.assignWhitePieces(grid);
            this.p1.color = 'white';
            console.log(`\n${this.p1.name} has been assigned to white (w) and will move first.`);
            this.p2.assignBlackPieces(grid);
            this.p2.color = 'black';
        } else {
            this.p2.assignWhitePieces(grid);
            this.p2.color = 'white';
            console.log(`${this.p2.name} has been assigned to white (w) and will move first.\n`);
            this.p1.assignBlackPieces(grid);
            this.p1.color = 'black';
        }
    }

    private produceEmptyBoard(): void {
        let cCnt = 0;
        for (const col of grid) {
            for (const cord of col) {
                this.gameState[cord] = [null, cCnt];
            }
            cCnt += 1;
        }
    }
}
