import { createContext, useState, ReactNode, useEffect } from 'react';
import useWebSocket, { ReadyState } from 'react-use-websocket';
import { JsonObject } from 'react-use-websocket/dist/lib/types';

interface GetGamePlayerUsernames extends JsonObject {
  type: string;
  initiatingUser: string;
  recievingUser: string;
}

type GameContextType = {
  initiatingUser: string;
  receivingUser: string;
  setInitiatingUser: (username: string) => void;
  setReceivingUser: (username: string) => void;
};

export const GameContext = createContext<GameContextType>({
  initiatingUser: '',
  receivingUser: '',
  setInitiatingUser: () => {},
  setReceivingUser: () => {},
});

type GameProviderProps = {
  children: ReactNode;
};

export const GameProvider: React.FC<GameProviderProps> = ({ children }) => {
  const [initiatingUser, setInitiatingUser] = useState<string>('');
  const [receivingUser, setReceivingUser] = useState<string>('');
  const [socketUrl, setSocketUrl] = useState('ws://localhost:3001');
  const { 
    sendMessage, 
    lastMessage,
    readyState 
  } = useWebSocket<GetGamePlayerUsernames>(socketUrl, { 
    onOpen: () => console.log('opened'), 
    shouldReconnect: (closeEvent) => true,
  });
  const connectionStatus = {
    [ReadyState.CONNECTING]: 'Connecting',
    [ReadyState.OPEN]: 'Open',
    [ReadyState.CLOSING]: 'Closing',
    [ReadyState.CLOSED]: 'Closed',
    [ReadyState.UNINSTANTIATED]: 'Uninstantiated',
  }[readyState];

  useEffect(() => {
    function handleIncomingData(data: GetGamePlayerUsernames) {
      const { type, initiatingUser, recievingUser } = data;
      if (type === 'game-player-usernames') {
        setInitiatingUser(initiatingUser);
        setReceivingUser(recievingUser);
      }
    }
    if (!initiatingUser && !receivingUser) {
      if (lastMessage !== null) {
        if (lastMessage.data instanceof Blob) {
          const reader = new FileReader();
          reader.onload = () => {
            if (typeof reader.result === 'string') {
              const data = JSON.parse(reader.result);
              handleIncomingData(data);
            }
          };
          reader.readAsText(lastMessage.data);
        } else {
          const data = JSON.parse(lastMessage.data);
          handleIncomingData(data);
        }
      }
      const message = JSON.stringify({type: 'get-game-players', gameId: ''})
      sendMessage(message);
    }
  }, [initiatingUser, lastMessage, receivingUser, sendMessage]);

  return (
    <GameContext.Provider value={{ initiatingUser, receivingUser, setInitiatingUser, setReceivingUser }}>
      {children}
    </GameContext.Provider>
  );
};
